%function Hd = Experiment-six-three
%EXPERIMENT-SIX-THREE 返回离散时间滤波器对象。

% MATLAB Code
% Generated by MATLAB(R) 9.10 and Signal Processing Toolbox 8.6.
% Generated on: 01-Dec-2023 09:36:36

% Butterworth Lowpass filter designed using FDESIGN.LOWPASS.

% All frequency values are in Hz.
Fs = 48000;  % Sampling Frequency

Fpass = 10000;       % Passband Frequency
Fstop = 16000;       % Stopband Frequency
Apass = 1;           % Passband Ripple (dB)
Astop = 100;         % Stopband Attenuation (dB)
match = 'stopband';  % Band to match exactly

% Construct an FDESIGN object and call its BUTTER method.
h  = fdesign.lowpass(Fpass, Fstop, Apass, Astop, Fs);
Hd = design(h, 'butter', 'MatchExactly', match);

% [EOF]

y1=rand(50,1);
subplot(211)
plot(y1)
title('原来的信号')
%对随机信号进行滤波
y2=filter(Hd,y1);
subplot(212)
plot(y2)
title('滤波后的信号')
